mapper.select:
select *, (select count(*) from activemember where activemember.activity = activity.id) as number from activity
where comy like "%" #{comy} "%"
limit #{startPage},#{num}

select *, (select count(*) from activemember where activemember.activity = activity.id) as number from activity where id in (select activity from activemember where student = #{student})
and comy like "%" #{comy} "%"
limit #{startPage}, #{num}

select *, (select count(*) from activemember where activemember.activity = activity.id) as number
from activity
where active like "%" #{active} "%"

    and comy like "%" #{comy} "%"

limit #{startPage}
, #{num}

select *, (select count(*) from activemember where activemember.activity = activity.id) as number
from activity
where date_format(time, '%Y-%m-%d') = date_format(#{time}, '%Y-%m-%d')

    and comy like "%" #{comy} "%"

limit #{startPage}
, #{num}

select *, (select count(*) from activemember where activemember.activity = activity.id) as number
from activity
where date_format(time, '%Y-%m') = date_format(#{time}, '%Y-%m')

    and comy like "%" #{comy} "%"

limit #{startPage}
, #{num}

select *, (select count(*) from activemember where activemember.activity = activity.id) as number
from activity
where date_format(time, '%Y') = date_format(#{time}, '%Y')

    and comy like "%" #{comy} "%"

limit #{startPage}
, #{num}

select *, (select count(*) from activemember where activemember.activity = activity.id) as number
from activity
where responsible like "%" #{responsible} "%"

    and comy like "%" #{comy} "%"

limit #{startPage}
, #{num}

select * from activity where time like "%" #{date} "%"
and comy like "%" #{comy} "%"
limit #{startPage},#{num}
