mapper.select:
select *, (select count(*) from activemember where activemember.activity = activity.id) as number
from activity
where id in (select activity from activemember where student = #{student}) limit #{startPage}
    , #{num}

select *, (select count(*) from activemember where activemember.activity = activity.id) as number
from activity
where active like "%" #{active} "%" limit #{startPage}
    , #{num}

select *, (select count(*) from activemember where activemember.activity = activity.id) as number
from activity
where date_format(time, '%Y-%m-%d') = date_format(#{time}, '%Y-%m-%d') limit #{startPage}
    , #{num}

select *, (select count(*) from activemember where activemember.activity = activity.id) as number
from activity
where date_format(time, '%Y-%m') = date_format(#{time}, '%Y-%m') limit #{startPage}
    , #{num}

select *, (select count(*) from activemember where activemember.activity = activity.id) as number
from activity
where date_format(time, '%Y') = date_format(#{time}, '%Y') limit #{startPage}
    , #{num}

select *, (select count(*) from activemember where activemember.activity = activity.id) as number
from activity
where responsible like "%" #{responsible} "%" limit #{startPage}
    , #{num}
