    , #{num}

select count(*)
from activity
where student = #{student}

select *
from activity
where date_format(time, '%Y-%m') = date_format(#{time}, '%Y-%m') limit #{startPage}
    , #{num}

select *
from activity
where date_format(time, '%Y') = date_format(#{time}, '%Y') limit #{startPage}
    , #{num}
