mapper.select:
select *,(select count(*) from member where member.department = department.name) as num from department
where find_in_set(comy,#{comy})
limit #{startPage},#{num};

select *, (select count(*) from member where member.department = department.name) as num
from department
where department.name like "%" #{name} "%"

    and find_in_set(comy,#{comy})

limit #{startPage}
, #{num};

select *, (select count(*) from member where member.department = department.name) as num
from department
where department.minister like "%" #{minister} "%"

    and find_in_set(comy,#{comy})

limit #{startPage}
, #{num};

select *, (select count(*) from member where member.department = department.name) as num
from department
where department.college like "%" #{college} "%"

    and find_in_set(comy,#{comy})

limit #{startPage}
, #{num};

select *, (select count(*) from member where member.department = department.name) as num
from department
where id = #{id}
