mapper.update:
update dormitory
set building = #{dormitory.building},
    number   = #{dormitory.number},
    manage   = #{dormitory.manage},
    comy     = #{dormitory.comy}
where building = #{dormitory.building}
  and number = #{dormitory.number}

mapper.delete:
delete
from dormitory
where building = #{building}
  and number = #{number}

mapper.select:
select *
from student
where bedroom_lou = #{dormitory.building}
  and bedroom_hao = #{dormitory.number} limit #{startPage}
    , #{num}

select count(*)
from student
where bedroom_lou = #{dormitory.building}
  and bedroom_hao = #{dormitory.number}

select *, (select count(*) from student where student.bedroom_lou = dormitory.building and bedroom_hao = dormitory.number) as num from dormitory
where comy like "%" #{comy} "%"
limit #{startPage},#{num}

select *,
(select count(*)
from student
where student.bedroom_lou = dormitory.building
and bedroom_hao = dormitory.number) as num
from dormitory
where manage = #{name}

    and comy like "%" #{comy} "%"

limit #{startPage}
, #{num}

