mapper.select:
select * from honour
where find_in_set(#{comy},comy)
limit #{startPage},#{num}

select *
from honour
where student = #{student}

    and find_in_set(#{comy},comy)

limit #{startPage}
, #{num}

select *
from honour
where type like "%" #{type} "%"

    and find_in_set(#{comy},comy)

limit #{startPage}
, #{num}

select *
from honour
where prize like "%" #{prize} "%"

    and find_in_set(#{comy},comy)

limit #{startPage}
, #{num}

select *
from honour
where date_format(time, '%Y-%m-%d') = date_format(#{time}, '%Y-%m-%d')

    and find_in_set(#{comy},comy)

limit #{startPage}
, #{num}

select *
from honour
where date_format(time, '%Y-%m') = date_format(#{time}, '%Y-%m')

    and find_in_set(#{comy},comy)

limit #{startPage}
, #{num}

select *
from honour
where date_format(time, '%Y') = date_format(#{time}, '%Y')

    and find_in_set(#{comy},comy)

limit #{startPage}
, #{num}

select *
from honour
where id = #{id};

select *
from honour
where name like "%" #{name} "%"

    and find_in_set(#{comy},comy)

limit #{startPage}
, #{num}

select * from honour where time like "%" #{date} "%"
and find_in_set(#{comy},comy)
limit #{startPage},#{num}
