mapper.select:
select *
from honour
where student = #{student} limit #{startPage}
    , #{num}

select *
from honour
where type like "%" #{type} "%" limit #{startPage}
    , #{num}

select *
from honour
where prize like "%" #{prize} "%" limit #{startPage}
    , #{num}

select *
from honour
where date_format(time, '%Y-%m-%d') = date_format(#{time}, '%Y-%m-%d') limit #{startPage}
    , #{num}

select count(*)
from honour
where student = #{student}

select *
from honour
where date_format(time, '%Y-%m') = date_format(#{time}, '%Y-%m') limit #{startPage}
    , #{num}

select *
from honour
where date_format(time, '%Y') = date_format(#{time}, '%Y') limit #{startPage}
    , #{num}
