mapper.select:
select *
from qingjia
where student = #{student} limit #{startPage}
    , #{num}

select count(*)
from qingjia
where student = #{student}

select *
from qingjia
where date_format(start_time, '%Y-%m-%d') = date_format(#{time}, '%Y-%m-%d')
   or date_format(end_time, '%Y-%m-%d') = date_format(#{time}, '%Y-%m-%d')
    limit #{startPage}
    , #{num}

select *
from qingjia
where date_format(start_time, '%Y-%m') = date_format(#{time}, '%Y-%m')
   or date_format(end_time, '%Y-%m') = date_format(#{time}, '%Y-%m')
    limit #{startPage}
    , #{num}

select *
from qingjia
where date_format(start_time, '%Y') = date_format(#{time}, '%Y')
   or date_format(end_time, '%Y') = date_format(#{time}, '%Y')
    limit #{startPage}
    , #{num}

select *
from qingjia
where name like "%" #{name} "%" limit #{startPage}
    , #{num}

select *
from qingjia
where clazz like "%" #{clazz} "%" limit #{startPage}
    , #{num}
