    and find_in_set(qingjia1.comy,#{comy})

limit #{startPage}
, #{num}

select *,(select count(*)
from qingjia qingjia2
where qingjia2.student = qingjia1.student
) as num
from qingjia qingjia1
where qingjia1.name like "%" #{name} "%"

    and find_in_set(qingjia1.comy,#{comy})

limit #{startPage}
, #{num}

select *,(select count(*)
from qingjia qingjia2
where qingjia2.student = qingjia1.student
) as num
from qingjia qingjia1
where clazz like "%" #{clazz} "%"

    and find_in_set(qingjia1.comy,#{comy})

limit #{startPage}
, #{num}

select *
from qingjia
where id = #{id};

select *,(select count(*) from qingjia qingjia2 where qingjia2.student = qingjia1.student ) as num from qingjia qingjia1 where (qingjia1.start_time like "%" #{date} "%" or qingjia1.end_time like "%" #{date} "%")
and find_in_set(qingjia1.comy,#{comy})
limit #{startPage},#{num}

select  * from qingjia where  TO_DAYS(start_time)=TO_DAYS(#{time})

    and find_in_set(comy,#{comy})

limit #{startPage},#{num}

