mapper.delete:
delete
from talk
where id = #{id} and teacher =#{teacher}

mapper.select:
select *
from talk
where teacher =#{teacher}
limit #{startPage},#{num};

select *
from talk
where student = #{student} and teacher =#{teacher}

select *
from talk
where teacher like "%" #{teacher} "%" limit #{startPage}
    , #{num}

select *
from talk
where time = #{time} limit #{startPage}
    , #{num}

select count(*)
from talk
where teacher =#{teacher}

select *
from talk
where student = #{student}and teacher =#{teacher} limit #{startPage}
    , #{num}

select count(*)
from talk
where student = #{student}  and teacher =#{teacher}

select count(*)
from talk
where time = #{time} and teacher =#{teacher}

select *
from talk
where date_format(time, '%Y-%m') = date_format(#{time}, '%Y-%m') limit #{startPage}
    , #{num}
