mapper.select:
select *, (select count(*) from talkmember where talkmember.type = talktype.type) as number from talktype


        and find_in_set(#{comy},comy)


limit #{startPage},#{num}

select *, (select count(*) from talkmember where talkmember.type = talktype.type) as number
from talktype
where id = #{id}

mapper.update:
update talktype
set type    = #{talkType.type},
    college = #{talkType.college},
    comy    = #{talkType.comy}
where id = #{talkType.id}
